import React, { Component } from 'react'

class Button extends Component {
    state = {
        isOpen: true,
        closeText: '收合',
        openText: '展開',
        class: 'btn',
    }
    toggleBtn = () => {
        this.setState({ isOpen: !this.state.isOpen });
      }
    render() {
        const { isOpen } = this.props;
        return (
            <div className={this.state.class}>{isOpen ? this.state.closeText : this.state.openText}</div>
        )
    }
}
export default Button;
