import React, { Component } from 'react';
import bannerImg from './imgs/banner.png';
import './App.css';

class App extends Component {
  state = {
    openAtStart: true,
    autoToggle: true,
    button: {
      closeText: '收合', // [string]
      openText: '展開', // [string]
      class: 'btn' // [string]
    },
    class: {
      closed: 'closed', // [string]
      closing: 'closing', // [string]
      opened: 'opened', // [string]
      opening: 'opening' // [string]
    },
    transition: true,
  }
  render() {
    return (
      <div className="App">
        <div class="banner">
          <a class="wrap" href="#">
            <img class="img" src={bannerImg} title="輸入廣告促銷說明文字" alt="輸入廣告促銷說明文字" />
          </a>
        </div>
      </div>
    );
  }
}

export default App;
