import React, { Component } from 'react';
import bannerImg from './imgs/banner.png';
import Button from './Button';

class Banner extends Component {
    state = {
        // openAtStart: true,
        openAtStart: false,
        autoToggle: true,
        button: {
            closeText: '收合',
            openText: '展開',
            class: 'btn',
        },
        bannerClasses: {
            closed: 'closed',
            closing: 'closing',
            opened: 'opened',
            opening: 'opening',
        },
        transition: false,
        // transition: true,
        transitioning: false,
        isOpen: true,
    };
    toggleBanner = () => {
        // this.setState({ isOpen: !this.state.isOpen });
        console.log(this);
    }
    openAtStart = () => {
    }
    render() {
        const { bannerClasses, isOpen } = this.state;
        // const classes = `banner ${this.state.transition ? 'transition' : ''} ${isOpen ? bannerClasses.opened : bannerClasses.closed}`;
        return (
            <div className={`banner`}>
                <a className="wrap">
                    <img className="img" src={bannerImg} title="輸入廣告促銷說明文字" alt="輸入廣告促銷說明文字" />
                </a>
                <Button btnName={this.props.button.class} toggle={this.toggleBanner} btnText={isOpen ? this.props.button.closeText : this.props.button.openText}></Button>
            </div>
        );
    }
}
export default Banner;
