import React, { Component } from 'react';
import bannerImg from './imgs/banner.png';
import Button from './Button';

class Banner extends Component {
    state = {
        bannerStat: this.props.openAtStart ? 0 : 2
    };
    toggleBanner = () => {
        if (this.props.transition) {
            if (this.state.bannerStat === 0 || this.state.bannerStat === 2) {
                this.setState({ bannerStat: this.state.bannerStat + 1 });
            } else if (this.state.bannerStat === 3) {
                this.setState({ bannerStat: 0 });
            }
        } else {

        }
    }
    bannerClasses = ['opened', 'closing', 'closed', 'opening'];
    render() {
        const { bannerStat } = this.state;
        // const classes = `banner ${this.state.transition ? 'transition' : ''} ${bannerStat ? bannerClasses.opened : bannerClasses.closed}`;
        return (
            <div className={`banner`}>
                <a className="wrap">
                    <img className="img" src={bannerImg} title="輸入廣告促銷說明文字" alt="輸入廣告促銷說明文字" />
                </a>
                <Button btnName={this.props.button.class} toggle={this.toggleBanner} btnText={bannerStat ? this.props.button.closeText : this.props.button.openText}></Button>
            </div>
        );
    }
}
export default Banner;
