import React, { Component } from 'react';
import bannerImg from './imgs/banner.png';
import Button from './Button';
import './css/main.css';

class App extends Component {
  defaultProps = {
    // openAtStart: true,
    openAtStart: false,
    autoToggle: true,
    button: {
      closeText: '收合',
      openText: '展開',
      class: 'btn',
    },
    bannerClasses: {
      closed: 'closed',
      closing: 'closing',
      opened: 'opened',
      opening: 'opening',
    },
    transition: false,
    // transition: true,
    transitioning: false,
    isOpen: true,
  };
  toggleBanner = () => {
    this.setState({ isOpen: !this.state.isOpen });
  }
  render() {
    const { bannerClasses, isOpen } = this.state;
    const classes = `banner ${this.state.transition ? 'transition' : ''} ${isOpen ? bannerClasses.opened : bannerClasses.closed}`;
    return (
      <div className={classes}>
        <a className="wrap">
          <img className="img" src={bannerImg} title="輸入廣告促銷說明文字" alt="輸入廣告促銷說明文字" />
        </a>
        <Button btnName={this.state.button.class} toggle={this.toggleBanner} btnText={isOpen ? this.state.button.closeText : this.state.button.openText}></Button>
      </div>
    );
  }
}

export default App;
