import React, { Component } from 'react'

class Button extends Component {
    state = {
        closeText: '收合',
        openText: '展開',
        class: 'btn',
    }
    render() {
        const { openAtStart, isOpen, toggleBtn } = this.props;
        return (
            <div className={this.state.class} onClick={toggleBtn}>{isOpen ? this.state.closeText : this.state.openText}</div>
        )
    }
}
export default Button;
