import React, { Component } from 'react';
import bannerImg from './imgs/banner.png';
import './css/main.css';

class App extends Component {

  state = {
    openAtStart: true,
    autoToggle: true,
    button: {
      closeText: '收合',
      openText: '展開',
      class: 'btn',
    },
    bannerClasses: {
      closed: 'closed',
      closing: 'closing',
      opened: 'opened',
      opening: 'opening',
    },
    transition: true,
    transitioning: false,
    isOpen: true,
    fullClassName: 'banner',
  };
  toggleBanner = () => {
    this.setState({
      isOpen: !this.state.isOpen,
      fullClassName: this.state.fullClassName + this.state.transition ? ' transition' : ''+''+this.state.openAtStart?this.state.bannerClasses.opened:this.state.bannerClasses.closed,
    });
  }
  render() {
    const { fullClassName } = this.state;
    const styleObj = { transition: .5 };
    // const classes = `banner ${this.state.transition ? 'transition' : ''} ${openAtStart ? bannerClasses.opened : bannerClasses.closed} ${isOpen ? bannerClasses.opening : bannerClasses.closing}`;
    return (
      <div className={fullClassName} style={styleObj}>
        <a className="wrap">
          <img className="img" src={bannerImg} title="輸入廣告促銷說明文字" alt="輸入廣告促銷說明文字" />
        </a>
        <div className={this.state.button.class} onClick={this.toggleBanner}>{this.state.isOpen ? this.state.button.closeText : this.state.button.openText}</div>
      </div>
    );
  }
}

export default App;
