import React, { Component } from 'react';
import bannerImg from './imgs/banner.png';
import './css/main.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { counter: 0 };
  }
  state = {
    // openAtStart: true,
    openAtStart: false,
    autoToggle: true,
    button: {
      closeText: '收合',
      openText: '展開',
      class: 'btn',
    },
    bannerClasses: {
      closed: 'closed',
      closing: 'closing',
      opened: 'opened',
      opening: 'opening',
    },
    transition: false,
    // transition: true,
    transitioning: false,
    isOpen: true,
  };
  toggleBanner = () => {
    this.state.transition ? this.setState({ transitioning: !this.state.transitioning }) : this.setState({ isOpen: !this.state.isOpen });;
  }
  toggleBtn = () => {
    this.setState({ isOpen: !this.state.isOpen });
  }
  render() {
    const { bannerClasses, isOpen } = this.state;
    const classes = `banner ${this.state.transition ? 'transition' : ''} ${isOpen ? bannerClasses.opened : bannerClasses.closed}`;
    return (
      <div className={classes}>
        <a className="wrap">
          <img className="img" src={bannerImg} title="輸入廣告促銷說明文字" alt="輸入廣告促銷說明文字" />
        </a>
        <div className={this.state.button.class} onClick={this.toggleBtn}>{this.state.isOpen ? this.state.button.closeText : this.state.button.openText}</div>
      </div>
    );
  }
}

export default App;
